import React from "react";
import { Link } from "react-router-dom";
import { Form, List, Row, Col, Card, Empty, Image } from "antd";
import DescriptionCloth from "../Descriptions/DescriptionCloth";
import empty from "../../../assets/box.png";
import "../css/AddRental.css";

export default class SelectCloth extends React.Component {
  state = {
    cardActive: "",
  };

  handleClick = (cloth_id) => {
    this.setState({ cardActive: cloth_id });
    this.props.onChange(cloth_id);
    if (this.props.clear === true) {
      this.setState({
        cardActive: "",
      });
    }
  };

  render() {
    return (
      <div>
        {this.props.cloths.length === 0 ? (
          <Empty
            image={empty}
            imageStyle={{
              height: 60,
            }}
            description={
              <span>
                No hay Prendas Registrar. <br />
                si los desea puede registrar una en el apartado de{" "}
                <Link to="/cloth">
                  <span> Prendas </span>
                </Link>
              </span>
            }
          ></Empty>
        ) : (
          <Form.Item label="Prendas" name="cloth">
            <List
              grid={{
                gutter: 16,
                xs: 1,
                sm: 1,
                md: 2,
                lg: 2,
                xl: 4,
                xxl: 4,
              }}
              pagination={{
                pageSize: 4,
                hideOnSinglePage: true,
                responsive: true,
              }}
              dataSource={this.props.cloths}
              renderItem={(cloth, index) => (
                <List.Item>
                  <Card
                    onClick={() => this.handleClick(cloth.id)}
                    hoverable
                    type="success"
                    className={
                      cloth.id === this.state.cardActive &&
                      this.props.clear === false
                        ? "card-cloth-select-actived"
                        : "card-cloth-select-deactived"
                    }
                  >
                    <Row gutter={[16, 8]}>
                      <Col xs={24} sm={24} md={24} lg={24} xl={24}>
                        <Image
                          src={cloth.image}
                          className="card-img-top"
                          alt="moda de referencia"
                          style={{ width: "50%" }}
                          fallback="data:image/png;base64,"
                        />
                      </Col>
                      <Col xs={24} sm={24} md={24} lg={24} xl={24}>
                        <div>
                          <DescriptionCloth cloth={cloth} />
                        </div>
                      </Col>
                    </Row>
                    <Card.Meta
                      className="mt-2"
                      description="www.confeccionesmaribel.com"
                    />
                  </Card>
                </List.Item>
              )}
            />
          </Form.Item>
        )}
      </div>
    );
  }
}
